import Checkboxes from "./components/Checkboxes";
import GenerateButton from "./components/GenerateButton";
import PasswordApp from "./components/PasswordApp";
import PasswordBar from "./components/PasswordBar";
import StrengthIndicator from "./components/StrengthIndicator";
import Text from "./components/Text";
import ShowPassword from "./components/ShowPassword";
import { useEffect, useReducer, useState } from "react";
import classes from "./css/app.module.css";
import generatePassword from "./passwordsymbols";
// import { symbols, uppercase, lowercase, numbers } from "./passwordsymbols";

// uppercase, lowercase, numebrs, symbol
const checkboxes = [
  "Include Uppercase Letters",
  "Include Lowercase Letters",
  "Include Numbers",
  "Include Symbols",
];
function reducer(state, action) {
  return { ...state, ...action };
}

function App() {
  let passwordLength = 0;
  const generatePassword = (length) => {
    return generatePassword(length, ...isChecked);
  };
  const [isChecked, dispatchCheckBox] = useReducer(reducer, {
    0: false,
    1: false,
    2: false,
    3: false,
  });
  const [password, setPassword] = useState("");

  // should be under a useEffect
  useEffect(() => {
    generatePassword;
  }, [generatePassword]);
  return (
    <PasswordApp>
      <div className={classes.text}>
        <Text></Text>
      </div>
      <ShowPassword password={password}></ShowPassword>
      <div className={classes["toggle-div"]}>
        <PasswordBar getLength={generatePassword}></PasswordBar>
        <Checkboxes
          checkboxes={checkboxes}
          dispatchHandler={dispatchCheckBox}
        ></Checkboxes>
        <StrengthIndicator></StrengthIndicator>
        <GenerateButton></GenerateButton>
      </div>
    </PasswordApp>
  );
}

export default App;
