import React from "react";
import classes from "../css/strengthindicator.module.css";
import { useEffect, useMemo } from "react";
import StrengthBar from "./StrengthBar";
const strengths = ["medium", "medium", "medium", "none"];
const StrengthIndicator = ({ strength }) => {
  useEffect(() => {
    switch (strength) {
      case "too weak":
        strengths.map((strength, idx) => {
          if (idx === 0) strengths[0] = "too weak";
          else strengths[idx] = "none";
        });
        break;
      case "weak":
        strengths.map((strength, idx) => {
          if (idx === 0 && idx === 1) strengths[idx] = "weak";
          else strengths[idx] = "none";
        });
        break;
      case "medium":
        strengths.map((strength, idx) => {
          if (idx !== strengths.length - 1) strengths[idx] = "medium";
          else strengths[idx] = "none";
        });
        break;
      case "strong":
        strengths.map((strength, idx) => (strengths[idx] = "strong"));
        break;
      default:
        break;
    }
  }, [strength, strengths]);
  return (
    <div className={classes["strength-div"]}>
      <p className={classes["strength"]}>STRENGTH</p>
      <div className={classes["strengthbar-div"]}>
        <p className={classes["strength-indicator"]}>{strength}</p>
        <div className={classes["strengthbar-div"]}>
          {strengths.map((strength, idx) => {
            return <StrengthBar key={idx} strength={strength}></StrengthBar>;
          })}
        </div>
      </div>
    </div>
  );
};

export default StrengthIndicator;
