function getRandomLower() {
  return String.fromCharCode(Math.floor(Math.random() * 26) + 97);
}
function getRandomUpper() {
  return String.fromCharCode(Math.floor(Math.random() * 26) + 65);
}
function getRandomNumber() {
  return String.fromCharCode(Math.floor(Math.random() * 10) + 48);
}

function getRandomSymbol() {
  const symbols = "!@#$%^&*(){}[]=<>/,.";
  return symbols[Math.floor(Math.random() * symbols.length)];
}
export default function passwordGenerator(
  obj = {
    length: 0,
    Uppercase: false,
    Lowercase: false,
    Numbers: false,
    Symbols: false,
  }
) {
  const { length, Uppercase, Lowercase, Numbers, Symbols } = obj;
  console.log(length, Uppercase, Lowercase, Numbers, Symbols);
  let generatedPassword = "";
  let variationsCount = [Uppercase, Lowercase, Numbers, Symbols].length;

  for (let i = 0; i < length + 1; i += variationsCount) {
    if (Numbers) {
      generatedPassword += getRandomNumber();
    }
    if (Uppercase) {
      generatedPassword += getRandomUpper();
    }
    if (Symbols) {
      generatedPassword += getRandomSymbol();
    }
    if (Lowercase) {
      generatedPassword += getRandomLower();
    }
  }

  const finalPassword = generatedPassword.slice(0, length + 1);

  return finalPassword;
}
